    private void btnBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBuscarActionPerformed
        String x = String.valueOf(0);
        lblId.setText(x);

        String Nome = txtNome.getText() + "%";
        List<Cliente> clientes = new ClienteDAO().findNome(Nome);

        DefaultTableModel model =(DefaultTableModel) tabelaCliente.getModel();
        model.setNumRows(0);

        if (clientes.isEmpty()){
            JOptionPane.showMessageDialog(rootPane, "Cliente n√£o encontrado!", "Erro", JOptionPane.ERROR_MESSAGE);
        } else {
            for (Cliente c : clientes) {
                model.addRow(new String[] {String.valueOf(c.getId()), c.getNome(), c.getCpf(), c.getCelular(), c.getCidade()});
            }
        }

        tabelaCliente.setModel(model);
        tabelaCliente.repaint();
    }//GEN-LAST:event_btnBuscarActionPerformed

